import Top from './components/Top.vue'
import Ticketing from './components/Ticketing.vue'
import Basic from './components/Basic.vue'
import Stock from './components/Stock.vue'
import Boat from './components/Boat.vue'
import AddUser from './components/AddUser.vue'
import ChangeUser from './components/ChangeUser.vue'
import QueryUser from './components/QueryUser.vue'
import Order from './components/Order.vue'
import Feedback from './components/Feedback.vue'
import Statistics from './components/Statistics.vue'
import Login from './components/Login.vue'
import "../icon/iconfont.css"

export default {
  name: 'app',
  data(){
	  return{
		  visible: false,
	  }
  },
  components: {
	Top,
    Ticketing,
	Basic,
	Stock,
	AddUser,
	ChangeUser,
	QueryUser,
	Order,
	Feedback,
	Statistics,
	Login,
	Boat
  },
  methods: {
      //第六步：定义获取子组件值的方法，该方法的参数就为子组件传递过来的值
      getCode(temp) {
        // 控制台输出看看能不能获取
        console.log(temp);
		if(temp == "1000"){
			this.$alert("登录成功", {
				confirmButtonText: '确定',
				type: 'success',
			});
			this.visible = !this.visible;
		}
		else{
			this.$alert("用户名或密码错误", {
				confirmButtonText: '确定',
				type: 'warning',
			});
		}
      },
	  outLogin(temp){
		  if(temp==1)
		  {
			  this.$alert("退出登录", {
			  	confirmButtonText: '确定',
			  	type: 'warning',
			  });
			  this.visible = !this.visible;
		  }
		  console.log("八嘎"+temp);
	  }
	},
	created() {
		document.title = "在线轮渡购票后台管理系统"	
	},
}
